// Obtener los adjuntos desde el input
let adjuntos = $input.first().json.body.Anexo;

// Inicializamos el string de la lista
let string_adjuntos = '';

// Validar que adjuntos exista y tenga al menos un elemento
if (adjuntos && Array.isArray(adjuntos) && adjuntos.length > 0) {
    // Recorremos cada item
    adjuntos.forEach(item => {
        // Si el item es válido lo agregamos
        let url = item; // Cambiar "item" por item.url si corresponde
        console.log(url);

        if (url) {
            string_adjuntos += `- ${url} \n`;
            //string_adjuntos += `- [${url}](${url}) \n`;
        }
    });
} else {
    // Si no hay adjuntos, podés definir un mensaje por defecto
    string_adjuntos = 'No se encontraron adjuntos.';
}

// Retornamos el resultado
return { "json": { string_adjuntos: string_adjuntos } };

