// Obtenemos el JSON de entrada
const data = items[0].json;

// Arrays para los resultados
const line_items = [];
const omitidos = [];

// Recorremos los productos del pedido
for (const p of data.pedido) {
  if (p.type === "variable") {
    // Producto variable
    if (p.id_variacion) {
      // Tiene variaci√≥n -> se incluye en el pedido
      line_items.push({
        product_id: p.codigo_producto,
        variation_id: p.id_variacion,
        quantity: p.cantidad,
        price: p.precio_unitario
      });
    } else {
      // No tiene variaci√≥n -> se omite y se registra el motivo
      omitidos.push({
        id_producto: p.codigo_producto,
        id_variation: p.id_variacion,
        nombre_producto: p.producto,
        motivo: "Producto de tipo variable sin id_variacion"
      });
    }
  } else {
    // Producto simple -> se incluye normalmente
    line_items.push({
      product_id: p.codigo_producto,
      quantity: p.cantidad,
      price: p.precio_unitario
    });
  }
}

// Construimos la orden
const order = {
