// Obtenemos el JSON de entrada (tu pedido)
const data = items[0].json;

// Creamos array de line_items a partir de cada producto
const line_items = data.pedido.map(p => {
  if (p.type === "variable") {
    // Producto variable -> se envía con variation_id
    return {
      product_id: p.codigo_producto,
      variation_id: p.id_variacion,
      quantity: p.cantidad,
      price: p.precio_unitario
    };
  } else {
    // Producto simple -> NO se envía variation_id
    return {
      product_id: p.codigo_producto,
      quantity: p.cantidad,
      price: p.precio_unitario
    };
  }
});

// Construimos la orden con datos quemados de billing/shipping
const order = {
  payment_method: "bacs",
  payment_method_title: "Transferencia bancaria",
  customer_note: "Pedido generado desde Ropofy", // 👈 nota fija
  set_paid: false,
  billing: {
    first_name: $('Webhook').first().json.body.first_name,
    last_name: "",
    address_1: $input.first().json.direccion,
    city: $input.first().json.ciudad,
    country: "AR",
    email: $('Webhook').first().json.body.email,
    phone: $('Webhook').first().json.body.phone
  },
  shipping: {
    first_name: $('Webhook').first().json.body.first_name,
    last_name: "",
    address_1: $input.first().json.direccion,
    city: $input.first().json.ciudad,
    country: "AR"
  },
  line_items
};

// Retornamos en el formato que n8n espera (un único item con el JSON)
return [
  {
    json: order
  }
];
